import React, { useState } from 'react';

const ItemDashboard = () => {
  const [sortedItems, setSortedItems] = useState([]);

  const fetchSortedItems = async () => {
    try {
      const response = await fetch('/api/sorted_items/');
      if (!response.ok) {
        throw new Error('Failed to fetch sorted items');
      }
      const data = await response.json();
      setSortedItems(data.sorted_items);
    } catch (error) {
      console.error('Error fetching sorted items:', error);
    }
  };

  const handleSortByName = async () => {
    try {
      const response = await fetch('/api/sorted_items_by_name/');
      if (!response.ok) {
        throw new Error('Failed to fetch sorted items');
      }
      const data = await response.json();
      setSortedItems(data.sorted_items);
    } catch (error) {
      console.error('Error fetching sorted items:', error);
    }
  };

  return (
    <>
      <button onClick={handleSortByName}>Sort by Name</button>

      <table>
        <thead>
          <tr>
            <th>SKU</th>
            <th>Name</th>
            <th>Tags</th>
            <th>Category</th>
            <th>In Stock</th>
            <th>Available Stock</th>
          </tr>
        </thead>
        <tbody>
          {sortedItems.map((item, index) => (
            <tr key={index}>
              <td>{item.sku}</td>
              <td>{item.name}</td>
              <td>{item.tags}</td>
              <td>{item.category}</td>
              <td>{item.in_stock}</td>
              <td>{item.available_stock}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );
};

export default ItemDashboard;
