import React, { useState, useEffect } from 'react';

const ItemDashboard = () => {
  const [counts, setCounts] = useState({});
  const [tableData, setTableData] = useState([]);

  useEffect(() => {
    const fetchCounts = async () => {
      try {
        const response = await fetch('http://localhost:8000/api/djangoApp/count/');
        if (!response.ok) {
          throw new Error('Failed to fetch counts');
        }
        const data = await response.json();
        setCounts(data);
        console.log(data)
      } catch (error) {
        console.error('Error fetching counts:', error);
      }
    };

    const fetchTableData = async () => {
      try {
        const response = await fetch('http://localhost:8000/api/djangoApp/');
        if (!response.ok) {
          throw new Error('Failed to fetch table data');
        }
        const data = await response.json();
        setTableData(data);
      } catch (error) {
        console.error('Error fetching table data:', error);
      }
    };

    fetchCounts();
    fetchTableData();
  }, []);

  return (
    <>
      <div>
        <h1>Total Count of Unique Categories: {counts.unique_categories_count}</h1>
        <h1>Total Count of Rows in the Table: {counts.total_rows_count}</h1>
      </div>
      <br />
      <table>
        <thead>
          <tr>
            <th>SKU</th>
            <th>Name</th>
            <th>Tags</th>
            <th>Category</th>
            <th>In Stock</th>
            <th>Available Stock</th>
          </tr>
        </thead>
        <tbody>
          {tableData.map((row, index) => (
            <tr key={index}>
              <td>{row.sku}</td>
              <td>{row.name}</td>
              <td>{row.tags}</td>
              <td>{row.category}</td>
              <td>{row.in_stock}</td>
              <td>{row.available_stock}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );
};

export default ItemDashboard;
